
              <form id="<?php echo $parent . "-" . $handle; ?>_form" class="field_form" action="#"><p><label for="<?php echo $parent . "-" . $handle; ?>"><?php echo $title; ?></label></p><div class="input_wrapper"><div class="minus button" onmousedown="( function( el ) { el.nextElementSibling.stepDown(); update_report(); } )(this)"></div><input id="<?php echo $parent . "-" . $handle; ?>" class="num_val default_<?php foreach( $columns as $key => $val ) { if ( $val == 'raw' ){ echo $default_vals[ $key ]; } } ?>" type="number" min=0 value=<?php foreach( $columns as $key => $val ) { if ( $val == 'raw' ){ echo $default_vals[ $key ]; } } ?> onfocus="this.select();" onchange="( function( el ) { update_report(); } )( this )" onkeyup="( function( el ) { update_report(); } )( this )"><div class="plus button" onmousedown="( function( el ) { el.previousElementSibling.stepUp(); update_report(); } )(this)"></div></div></form>
